import React, { Component } from "react";
const base64prefix = "data:image/png;base64, "
class Plot extends Component {
  state = {
    url: ""
  };

  componentDidMount() {
    this.userData = JSON.parse(localStorage.getItem("plot_url"));
    bytes(this.userData.file_location).decode('utf-8')
    if (localStorage.getItem("plot_url")) {
      this.setState({
        url: this.userData.file_location
      });
    }
  }

  render() {
    return (
      <div className="container">
        <div>
          <img src={base64prefix+this.state.url} alt="img" />
        </div>
      </div>
    );
  }
}

export default Plot;
